package io.zipkin.scribe;

import io.zipkin.Annotation;
import io.zipkin.BinaryAnnotation;
import io.zipkin.Constants;
import io.zipkin.Endpoint;
import io.zipkin.Span;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;
import org.junit.Test;

import static java.util.Arrays.asList;
import static org.assertj.core.api.Assertions.assertThat;

public class ScribeSpanConsumerTest {

  Endpoint zipkinQuery = Endpoint.builder()
      .ipv4((127 << 24) | 1)
      .port((short) 9411)
      .serviceName("zipkin-query").build();

  /** Finagle's zipkin tracer breaks on a column width with a trailing newline */
  @Test
  public void decodesSpanGeneratedByFinagle() {
    List<Span> spans = new ArrayList<>();

    new ScribeSpanConsumer(input -> input.forEach(spans::add))
        .log(asList(LogEntry.builder().category("zipkin")
                .message(
                    "\n"
                        + "\n")
                .build())
        );

    char[] as = new char[2048];
    Arrays.fill(as, 'a');
    String reallyLongAnnotation = new String(as);

    assertThat(spans).containsOnly(
        Span.builder()
            .traceId(-6054243957716233329L)
            .name("getTracesByIds")
            .id(-3615651937927048332L)
            .parentId(-6054243957716233329L)
            .annotations(asList(
                Annotation.builder()
                    .timestamp(1442493420635000L)
                    .value(Constants.SERVER_RECV)
                    .endpoint(zipkinQuery)
                    .build(),
                Annotation.builder()
                    .timestamp(1442493420747000L)
                    .value(reallyLongAnnotation)
                    .endpoint(zipkinQuery)
                    .build(),
                Annotation.builder()
                    .timestamp(1442493422583586L)
                    .value("Gc(9,0.PSScavenge,2015-09-17 12:37:02 +0000,304.milliseconds+762.microseconds)")
                    .endpoint(zipkinQuery)
                    .build(),
                Annotation.builder()
                    .timestamp(1442493422680000L)
                    .value(Constants.SERVER_SEND)
                    .endpoint(zipkinQuery)
                    .build()
            ))
            .binaryAnnotations(asList(
                BinaryAnnotation.builder()
                    .key("srv/finagle.version")
                    .value(new byte[]{'6', '.', '2', '8', '.', '0'})
                    .type(BinaryAnnotation.Type.STRING)
                    .endpoint(Endpoint.builder(zipkinQuery).port((short) 0).build())
                    .build(),
                BinaryAnnotation.builder()
                    .key("srv/mux/enabled")
                    .value(new byte[]{1})
                    .type(BinaryAnnotation.Type.BOOL)
                    .endpoint(Endpoint.builder(zipkinQuery).port((short) 0).build())
                    .build(),
                BinaryAnnotation.builder()
                    .key(Constants.SERVER_ADDR)
                    .value(new byte[]{1})
                    .type(BinaryAnnotation.Type.BOOL)
                    .endpoint(zipkinQuery).build(),
                BinaryAnnotation.builder()
                    .key(Constants.CLIENT_ADDR)
                    .value(new byte[]{1})
                    .type(BinaryAnnotation.Type.BOOL)
                    .endpoint(Endpoint.builder(zipkinQuery)
                        .port(/* Notice high port numbers go negative! */ (short) -1696).build())
                    .build(),
                BinaryAnnotation.builder()
                    .key("numIds") // odd annotation added by legacy zipkin query server
                    .value(new byte[]{0, 0, 0, 1})
                    .type(BinaryAnnotation.Type.I32)
                    .endpoint(zipkinQuery).build()
            ))
            .debug(false)
            .build());
  }
}
